import React, { useState, useRef } from "react";
import "./App.css";
import SearchDataJSON from "./searchData.json";
function App() {
  const scrollContainerRef = useRef(null);
  const [searchData, setSearchData] = useState([...SearchDataJSON]);
  const [searchInput, setSearchInput] = useState("");
  const [filteredSearchData, setFilteredData] = useState([]);
  console.log("searchData", searchData);

  const handleSearchInputChange = (e) => {
    const searchValue = e.target.value.toLowerCase();
    setSearchInput(searchValue);

    const filtered = searchData.filter((item) => {
      // Check if any key or item in the 'items' array contains the search query
      return Object.entries(item).some(
        ([key, value]) =>
          typeof value === "string" &&
          (value.toLowerCase().includes(searchValue) ||
            (key === "items" &&
              value.some((itemValue) =>
                itemValue.toLowerCase().includes(searchValue)
              )))
      );
    });
    setFilteredData(filtered);
  };
  const handleCardHover = (index) => {
    const cardElement = document.getElementById(`card-${index}`); // Get the card element by index
    cardElement.scrollIntoView({ behavior: "smooth", block: "nearest" }); // Scroll the card into view
  };

  const highlightSearchTerm = (text) => {
    if (!searchInput) return text; // returns the original text without any highlighting.
    const regex = new RegExp(`(${searchInput})`, "gi");
    console.log("regular expression---", regex);
    return text
      .toString()
      .split(regex)
      .map((part, index) => {
        console.log("part---", part, index);
        return index % 2 === 0 ? (
          part
        ) : (
          <span style={{ color: "blue" }}>{part}</span>
        );
      });
  };
  console.log("filtered data", filteredSearchData);
  return (
    <div className="container">
      <div>
        <b>Parspec Assignment</b>
      </div>
      <div className="search-container">
        <input
          type="text"
          placeholder="Search..."
          style={{ width: "100%", borderRadius: "10px", padding: "10px" }}
          //value={searchQuery}
          onChange={handleSearchInputChange}
        />
      </div>
      <div
        style={{ width: "50%", height: "400px", overflow: "scroll" }}
        ref={scrollContainerRef}
      >
        {filteredSearchData.length ? (
          filteredSearchData.map((item, index) => (
            <div
              key={index}
              id={`card-${index}`}
              onMouseEnter={() => handleCardHover(index)}
            >
              <div className="card-container">
                {Object.entries(item).map(([key, value]) => (
                  <p key={key}>{highlightSearchTerm(value)}</p>
                ))}
              </div>
            </div>
          ))
        ) : (
          <div>No Results Found</div>
        )}
      </div>
    </div>
  );
}

export default App;
