import React, { useState } from "react";
import "./App.css";
import SearchDataJSON from "./searchData.json";
function App() {
  const [searchData, setSearchData] = useState([...SearchDataJSON]);
  const [searchInput,setSearchInput] = useState("");
  const [filteredSearchData,setFilteredData] = useState([])
  console.log("searchData", searchData);

  const handleSearchInputChange = (e) =>{
   console.log("value searched---",e.target.value)
   const filtered = searchData.filter((item) => {
    // Convert object keys to an array and check if any of the values contain the search query
    return Object.values(item).some((value) =>
      typeof value === "string" && value.toLowerCase().includes(e.target.value.toLowerCase())
    );
  });
    setFilteredData(filtered); 
  }
console.log("filtered data",filteredSearchData)
  return (
    <div className="container">
      <div>
        <b>Parspec Assignment</b>
      </div>
      <div>
      <input
        type="text"
        placeholder="Search..."
        //value={searchQuery}
        onChange={handleSearchInputChange}
      />
      </div>
      <div>
      {filteredSearchData.map((item, index) => (
          <div key={index}>
            {/* Display the properties of each object */}
            {Object.entries(item).map(([key, value]) => (
              <p key={key}>{`${key}: ${value}`}</p>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
